import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles({
    root: {
      width: 500,
    },
    title:{
        textAlign:'center'
    },
    ImgLink: {
        marginLeft:'40%',
        marginTop:'-25%',
        position:'absolute',
        zIndex:'2',
        maxWidth:'15rem',
        maxHeight:'25rem',
        width:'100%',
        height:'100%'
    }
  });

function ShowImagesFUll(props) {
    const {imgLink} = props;
    const classes = useStyles();
    const [ sr,setSr] = useState()

    let blob = new Blob([imgLink.data],{type:'image/jpeg'})
    let links = URL.createObjectURL(blob)

    const promise = new Promise((resolve, reject) => {
        const reader = new FileReader()

        reader.readAsDataURL(blob)

        reader.onload = () => {
          if (!!reader.result) {
            resolve(reader.result)
          }
          else {
            reject(Error("Failed converting to base64"))
          }
        }

      })
      promise.then(result => {
        setSr(result)
        // dispatch or do whatever you need with result
      }, err => {
        console.log(err)
      })

    return (
        <div className={classes.ImgLink}>
            <img src={sr} />
        </div>
    )
}

export default ShowImagesFUll;
